mapper.select:

SELECT CALLBACK_URL FROM NPSPEN0001
WHERE REQUEST_ID = #{requestId}
  AND REQ_DT = (
    SELECT REQ_DT FROM (
       SELECT
          MAX(REQ_DT) AS REQ_DT
       FROM NPSPEN0001
       WHERE REQUEST_ID = #{requestId}
    ) TMP_TABLE
  )

mapper.insert:

INSERT INTO NPSPEN0001 (REQUEST_ID, REQ_DT, CALLBACK_URL, RES_DT, REG_DT)
VALUES (
  #{requestId}
  , #{reqDt}
  , #{callbackUrl}
  , #{resDt}
  , #{regDt}
)

mapper.update:

UPDATE NPSPEN0001
  SET RES_DT = #{resDt}
WHERE REQUEST_ID = #{requestId}
  AND REQ_DT = (
    SELECT REQ_DT FROM (
       SELECT
          MAX(REQ_DT) AS REQ_DT
       FROM NPSPEN0001
       WHERE REQUEST_ID = #{requestId}
     ) TMP_TABLE
  )
